import React, { useState, useEffect } from 'react'
import { Form, Button, Alert } from 'react-bootstrap'
import Axios from 'axios';
import Table from 'react-bootstrap/Table';

export default function NoticeList() {

    const [notices, setNotices] = useState([]);

    const getHostels = () => {
        Axios.get("http://localhost:3001/getData/", { params: { table: "notice_board" } }).then((response) => {
            setNotices(response.data);
        });
    };

    useEffect(() => {
        getHostels();
    }, []);

    const getLess = (s)=>{
        if(s.length<10) return s;
        else return s.substring(0,7)+"...";
    }

    const handleClick = e => {
        e.preventDefault();
        const id = e.target.id;
        setRoomID(rooms[id].Room_ID);
        const arr = modalShow;
        arr[id] = true;
        setModalShow(arr);
        setPublicData({...publicData,modalShow: arr});
    
      };

    return (
        <>
            <div className="shadow p-4" style={{
                width: "85%",
                border: "3px solid lightGray",
                marginLeft: "auto",
                marginRight: "auto",
            }} >

                <Table striped bordered hover>
                    <thead style={{
                        color: "blue",
                        letterSpacing: "2px",
                        font: "normal 20px/1 Comic Sans MS,Verdana, Helvetica",
                    }}>
                        <tr>
                            <th>Date Time</th>
                            <th>Title</th>
                            <th>Hostel ID</th>
                            <th>Priority</th>
                            <th>Description</th>
                            <th>Edit</th>
                            <th>Delete</th>
                        </tr>
                    </thead>
                    <tbody>

                        {notices.map((val, idx) => {
                            return <React.Fragment key={idx}>
                                    <tr>
                                        <td>{val.DateTime}</td>
                                        <td>{getLess(val.Title)}</td>
                                        <td>{val.Hostel_ID}</td>
                                        <td>{val.Priority}</td>
                                        <td>{getLess(val.Description)}</td>
                                        <td><Button id={val} key={idx} variant="link" onClick={e => handleClick(e)} >Edit </Button></td>
                                        <td>del</td>
                                    </tr>
                    
                                    </React.Fragment>
                            }
                        )}
                        
                    </tbody>
                </Table>

            </div>
            <br /><br />
        </>
    )
}
