                            else if (value.length > 10)
                                msg = "Too long!";
                            setError({ ...error, reg2: msg });
                        }} />
                        <span className="text-danger">{error.reg2}</span>
                    </Form.Group>

                    <Button variant="primary" type="submit">
                        Swap Room
                    </Button>
                </Form>
            )}
        </div>
        <br/><br/>
        </>
    )
}
