import React, { useEffect, useState } from 'react'
import { Form, Button, Alert } from 'react-bootstrap'
import Axios from 'axios';

export default function Profile() {

  const userInfo = 2018331008;
  const [show, setShow] = useState(false);
  const [finalMsg, setFinalMsg] = useState("");
  const [error, setError] = useState({
    reg_err:"",
    std_name_err:"",
    password_err:"",
    dept_err:"",
    merit_err:"",
    email_err:"",
    hostelID_err:"",
    roomID_err:"",
    phone_err:"",
    guardian_name_err:"",
    guardian_address_err:"",
    guardian_phone_err:"",
})

const [std, setStd] = useState({
  reg: "",
  std_name: "",
  password: "",
  dept: "",
  merit: "",
  email: "",
  hostelID: "",
  roomID: "",
  phone: "",
  roleID:"1",
  roleTag: "Student",
  guardian_name: "",
  guardian_address:"",
  guardian_phone: "",
}) 



  const handleSubmit = (event) => {

    console.log("User update");
    event.preventDefault();
    const isEmpty = Object.values(error).every(x => x === null || x === "");
    console.log(isEmpty);
    console.log(error);
    
    if (isEmpty) {
            Axios.put("http://localhost:3001/update/hostel", {
                hostel_id: hostel.id,
                type: hostel.type,
                name: hostel.name,
                address: hostel.address,
                contact: hostel.contact,
                occupied_seats: hostel.occupied_seats,
                total_seats: hostel.total_seats,
    
            }).then((response) => {
                if(response.data === "error"){
                    setFinalMsg("Something Error!!");
                } else {
                    setFinalMsg("Hostel Updated successfully...");
                }
                setShow(true);
            }).catch((e) => alert(e));
            
    }
    console.log(hostel);

};


  return (
    <div className="shadow p-4" style={{
      width: "60%",
      border: "3px solid lightGray",
      marginLeft: "auto",
      marginRight: "auto",
  }} >
    <Alert show={show} variant="success">
    <Alert.Heading>Profile Updated!!</Alert.Heading>
    <p>
        {finalMsg}
    </p>
    <hr />
    <div className="d-flex justify-content-end">
        <Button onClick={() => { setShow(false); window.location.reload(); }} variant="outline-success">
            Close!
        </Button>
    </div>
  </Alert>
  {!show && (
    <Form onSubmit={handleSubmit}>
        <Form.Group className="mb-3" controlId="reg">
            <Form.Label>Registration No</Form.Label>
            <Form.Control type="number" value={std.reg} readOnly />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Password</Form.Label>
            <Form.Control type="text" value={std.password} onChange={(event) => {
              const value = event.target.value;
              let msg = "";
              if (value.length === 0)
              msg = "* field is required";
              else if (value.length < 5)
              msg = "Password should be at least 5 digit!";
              setStd({...std,password:value});
              setError({...error,password_err:msg});
              }} 
            />
          <span className="text-danger">{this.state.password_err}</span>
        </Form.Group>
        <Form.Group className="mb-3">
            <Form.Label>Name</Form.Label>
            <Form.Control type="text" value = {std.std_name} onChange={(event) => {
                const value = event.currentTarget.value;
                setStd({ ...std, std_name: value })
                let msg = "";
                if (value.length === 0)
                    msg = "* field is required";
                setError({ ...error, std_name_error: msg });
            }}>
            </Form.Control>
            <span className="text-danger">{error.type}</span>
        </Form.Group>
        <Form.Group className="mb-3">
            <Form.Label>Depertment</Form.Label>
            <select className="form-select" value={std.dept} onChange={(event)=> {
                const value = event.currentTarget.value;
                let msg="";
                if(value.length === 0) msg="* field is required";
                setStd({...std,dept:value});
                setError({...error, dept_err:msg});
            }}>
            <option selected value={std.dept}>Department</option>
            <option value="1">CSE</option>
            <option value="2">EEE</option>
            <option value="3">SWE</option>
            <span className="text-danger">{error.dept_err}</span>
        </select>
        </Form.Group>
        <Form.Group className="mb-3">
            <Form.Label>Merit position</Form.Label>
            <Form.Control type="text" placeholder='merit position' value = {std.merit} onChange={(event) => {
                const value = event.target.value;
                setStd({ ...std, merit: value });
                let msg = "";
                if (value.length === 0)
                    msg = "* field is required";
                setError({ ...error, merit_err: msg });
            }} />
            <span className="text-danger">{error.merit_err}</span>
        </Form.Group>

        <Form.Group className="mb-3" controlId="hostelAddress">
            <Form.Label>Address</Form.Label>
            <Form.Control as="textarea" rows={3} value = {hostel.address} placeholder="Address..." onChange={(event) => {
                const value = event.target.value;
                setHostel({ ...hostel, address: value });
                let msg = "";
                if (value.length === 0)
                    msg = "* field is required";
                setError({ ...error, address: msg });
            }} />
            <span className="text-danger">{error.address}</span>
        </Form.Group>
        <Form.Group className="mb-3" controlId="hostelContact">
            <Form.Label>Contact</Form.Label>
            <Form.Control type="tel" value = {hostel.contact} placeholder="Office contact number: 01...." onChange={(event) => {
                const value = event.target.value;
                setHostel({ ...hostel, contact: value });
                let msg = "";

                const regex = /^[0][1][0-9]{9}$/i;
                if (value.length === 0)
                    msg = "* field is required";
                else if (value.length !== 11)
                    msg = "Mobile Number must have to be 11 Digits";
                else if (!regex.test(value))
                    msg = "Invalid format!";

                setError({ ...error, contact: msg });
            }} />
            <span className="text-danger">{error.contact}</span>
        </Form.Group>
        <Button variant="primary" type="submit">
            Update Hostel
        </Button>
    </Form>
  )}
  </div>
  )
}
