import React, { useState, useContext } from 'react'
import { Form, Button, Alert } from 'react-bootstrap'
import Axios from 'axios';

//import { PublicContex } from './PublicContext';

export default function UpdateHostel() {
    //const [publicData, setPublicData] = useContext(PublicContex);
    const [show, setShow] = useState(false);
    const [update, setUpdate] = useState(false);

    const [hostelData, setHostelData] = useState();
    const [id, setId] = useState();
    const [hostel, setHostel] = useState({
        id: "-1",
        type: "",
        name: "",
        address: "",
        contact: "",
        total_seats: 0,
    })

    const [error, setError] = useState({
        id: "* field is required",
        type: "* field is required",
        name: "* field is required",
        address: "* field is required",
        contact: "* field is required",
        total_seats: "* field is required",
    })
    const [error2, setError2] = useState({
        id: "* field is required",
    })

    const handleSubmit = (event) => {

        event.preventDefault();

        const isEmpty = Object.values(error2).every(x => x === null || x === "");
        console.log(isEmpty);
        console.log(error2.id);
        if (isEmpty) {
            console.log("dukse"+ id);
            Axios.get("http://localhost:3001/getData/hostel", { params: { hostel_id: id } }).then((response) => {
                if(response.data === "error"){
                    console.log("baaal error");
                    setError({ ...error2, id: "You have entered an Invalid id!!..." });
                } else {
                    setUpdate(true);
                    let data = JSON.stringify(response.data[0]);
                    console.log("res data");
                    console.log(data);
                    setHostelData(data);
                    // let Type = data.type;
                    // setHostel({ ...hostel, type: JSON.stringify(data.Type) });
                    // setHostel({ ...hostel, name: data.Name });
                    // setHostel({ ...hostel, address: data.Address });
                    //setHostel({ ...hostel, contact: data.Contact });
                    //setHostel({ ...hostel, occupied_seats: data.Occupied_Seats });
                    //setHostel({ ...hostel, total_seats: data.Total_Seats });
                    console.log("hostel data");
                    console.log(hostel);
                    // console.log("deleting");
                    // Axios.delete(`http://localhost:3001/delete/hostel/${hostel.id}`).then((response) => {
                    // if(response.data === "error"){
                    //     setHostel({ ...hostel, finalTxt: "Opps!! Something wrong!! Try agin later..." });
                    // } else {
                    //     setHostel({ ...hostel, finalTxt: "Hostel removed successfully..." });
                    // }
                    // setShow(true);
                    // }).catch((e) => alert(e));

                    setUpdate(true);
                }
            }).catch((e) => alert(e));
        }
    };

    const handleSubmit2 = (event) => {

        console.log("now in handlesubmit2");
        event.preventDefault();
        //check erro?
        const isEmpty = Object.values(error).every(x => x === null || x === "");
        console.log(isEmpty);
        
        if (isEmpty) {
            // Axios.post("http://localhost:3001/add/hostel", {
            //     hostel_id: hostel.id,
            //     name: hostel.name,
            //     type: hostel.type,
            //     address: hostel.address,
            //     contact: hostel.contact,
            //     total_seats: hostel.total_seats,
            //     occupied_seats: 0,
            // }).then(() => {
            //     setShow(true);
            //     //alert("hostel added");
            //     console.log("hostel added");
            // }).catch((e) => alert(e));

            setShow(true);
        }
        console.log(hostel);

    };

    return (
        <>
        <div className="shadow p-4" style={{
            width: "60%",
            border: "3px solid lightGray",
            marginLeft: "auto",
            marginRight: "auto",
        }} >
            <Alert show={show} variant="success">
                <Alert.Heading>How's it going?!</Alert.Heading>
                <p>
                    Hostel Updated successfully...
                </p>
                <hr />
                <div className="d-flex justify-content-end">
                    <Button onClick={() => { setShow(false); window.location.reload(); }} variant="outline-success">
                        Close!
                    </Button>
                </div>
            </Alert>

            {!show && !update && (
                <Form onSubmit={handleSubmit}>
                    <Form.Group className="mb-3" controlId="hostelID">
                        <Form.Label>Hostel ID</Form.Label>
                        <Form.Control type="number" onChange={(event) => {
                            const value = event.target.value;
                            console.log("dhur baal"+value);
                            setId(value);
                            let msg = "";
                            if (value.length === 0)
                                msg = "* field is required";
                            else if (Number(value) <= 0 || !Number.isInteger(Number(value)))
                                msg = "Invalid hostel ID!"
                                console.log("msg = "+msg);
                            setError2({ ...error2, id: msg });
                        }}  />
                        <span className="text-danger">{error2.id}</span>
                    </Form.Group>
                    <Button variant="primary" type="submit">
                        Select Hostel
                    </Button>
                </Form>
            )}

            {!show && update && (
                <Form onSubmit={handleSubmit2}>
                    <Form.Group className="mb-3" controlId="hostelID">
                        <Form.Label>Hostel ID</Form.Label>
                        <Form.Control type="number" value={id} readOnly />
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="hostelName">
                        <Form.Label>Name</Form.Label>
                        <Form.Control type="text" placeholder="Hostel Name" value = {hostelData.Name} onChange={(event) => {
                            const value = event.target.value;
                            setHostel({ ...hostel, name: value });
                            let msg = "";
                            if (value.length === 0)
                                msg = "* field is required";
                            else if (value.length < 2)
                                msg = "Too short!";
                            setError({ ...error, name: msg });
                        }} />
                        <span className="text-danger">{error.name}</span>
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="hostelType">
                        <Form.Label>Type</Form.Label>
                        <Form.Select value = {hostelData.Type} onChange={(event) => {
                            const value = event.currentTarget.value;
                            setHostel({ ...hostel, type: value })
                            let msg = "";
                            if (value.length === 0)
                                msg = "* field is required";
                            setError({ ...error, type: msg });
                        }}>
                            <option>Select Type</option>
                            <option value="male" >Male</option>
                            <option value="female">Female</option>
                        </Form.Select>
                        <span className="text-danger">{error.type}</span>
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="hostelSeats">
                        <Form.Label>Total Seats</Form.Label>
                        <Form.Control type="number" placeholder='Total number of seats' value = {hostelData.Total_Seats} onChange={(event) => {
                            const value = event.target.value;
                            setHostel({ ...hostel, total_seats: value });
                            let msg = "";
                            if (value.length === 0)
                                msg = "* field is required";
                            else if (Number(value) <= 0)
                                msg = "Total number of seats must be greater than zero!";
                            else if (!Number.isInteger(Number(value)))
                                msg = "Invalid number!"
                            else if (Number.isInteger(Number(value)) < Number.isInteger(Number(hostelData.Occupied_Seats)))
                                msg = "Total seats can't be less than occupied seats!";
                            setError({ ...error, total_seats: msg });
                        }} />
                        <span className="text-danger">{error.total_seats}</span>
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="hostelAddress">
                        <Form.Label>Address</Form.Label>
                        <Form.Control as="textarea" rows={3} value = {hostelData.Address} placeholder="Address..." onChange={(event) => {
                            const value = event.target.value;
                            setHostel({ ...hostel, address: value });
                            let msg = "";
                            if (value.length === 0)
                                msg = "* field is required";
                            setError({ ...error, address: msg });
                        }} />
                        <span className="text-danger">{error.address}</span>
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="hostelContact">
                        <Form.Label>Contact</Form.Label>
                        <Form.Control type="tel" value = {hostelData.Contact} placeholder="Office contact number: 01...." onChange={(event) => {
                            const value = event.target.value;
                            setHostel({ ...hostel, contact: value });
                            let msg = "";

                            const regex = /^[0][1][0-9]{9}$/i;
                            if (value.length === 0)
                                msg = "* field is required";
                            else if (value.length !== 11)
                                msg = "Mobile Number must have to be 11 Digits";
                            else if (!regex.test(value))
                                msg = "Invalid format!";

                            setError({ ...error, contact: msg });
                        }} />
                        <span className="text-danger">{error.contact}</span>
                    </Form.Group>
                    <Button variant="primary" type="submit">
                        Add Hostel
                    </Button>
                </Form>
            )}
        </div>
        <br/><br/>
        </>
    )
}
