import React, { useState, useEffect } from 'react'
import { Form, Button, Alert } from 'react-bootstrap'
import Axios from 'axios';

export default function NoticeList() {
    
    const [notices, setNotices] = useState([]);

    const getHostels = () => {
    Axios.get("http://localhost:3001/getData/", { params: { table: "hostenotice_board" } }).then((response) => {
        setNotices(response.data);
        });
    };

    useEffect(() => {
        getHostels();
    },[]);

    return (
        <>
        <div className="shadow p-4" style={{
            width: "60%",
            border: "3px solid lightGray",
            marginLeft: "auto",
            marginRight: "auto",
        }} >
            
        <table border={1}>
            <thead>
                <tr>
                    <th> Date Time </th>
                    <th> Title </th>
                    <th> Hostel ID</th>
                    <th> Priority</th>
                    <th> Description </th>
                </tr>
            </thead>

        </table>

        </div>
        <br/><br/>
        </>
    )
}
